import React, { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import { FaUsers, FaCopy, FaUserPlus, FaComments, FaMicrophone, FaMicrophoneSlash, FaSmile } from 'react-icons/fa';
import './WatchParty.css';

// Mock WebSocket for demo purposes
class MockWebSocket {
  constructor(url) {
    this.url = url;
    this.onmessage = null;
    this.onopen = null;
    this.onclose = null;
    this.onerror = null;
    
    // Simulate connection opening
    setTimeout(() => {
      if (this.onopen) this.onopen({ type: 'open' });
    }, 500);
  }
  
  send(data) {
    console.log('WebSocket message sent:', data);
    
    // Simulate receiving a response
    setTimeout(() => {
      if (this.onmessage) {
        const parsedData = JSON.parse(data);
        
        // Simulate different types of responses
        if (parsedData.type === 'join') {
          this.simulateUserJoined(parsedData.username);
        } else if (parsedData.type === 'playback') {
          this.simulatePlaybackSync(parsedData);
        } else if (parsedData.type === 'chat') {
          this.simulateChatMessage(parsedData);
        } else if (parsedData.type === 'reaction') {
          this.simulateReaction(parsedData);
        }
      }
    }, 300);
  }
  
  close() {
    if (this.onclose) this.onclose({ type: 'close' });
  }
  
  // Simulation helpers
  simulateUserJoined(username) {
    const response = {
      type: 'user_joined',
      username,
      timestamp: new Date().toISOString(),
      userId: Math.floor(Math.random() * 1000)
    };
    
    if (this.onmessage) {
      this.onmessage({ data: JSON.stringify(response) });
    }
  }
  
  simulatePlaybackSync(data) {
    const response = {
      type: 'playback_update',
      action: data.action,
      currentTime: data.currentTime,
      username: data.username,
      timestamp: new Date().toISOString()
    };
    
    if (this.onmessage) {
      this.onmessage({ data: JSON.stringify(response) });
    }
  }
  
  simulateChatMessage(data) {
    const response = {
      type: 'chat_message',
      message: data.message,
      username: data.username,
      timestamp: new Date().toISOString(),
      messageId: Math.floor(Math.random() * 10000)
    };
    
    if (this.onmessage) {
      this.onmessage({ data: JSON.stringify(response) });
    }
  }
  
  simulateReaction(data) {
    const response = {
      type: 'reaction',
      emoji: data.emoji,
      username: data.username,
      timestamp: new Date().toISOString()
    };
    
    if (this.onmessage) {
      this.onmessage({ data: JSON.stringify(response) });
    }
  }
}

