    { id: 1, username: 'You (Host)', isHost: true, isSelf: true }
  ]);
  const [chatMessages, setChatMessages] = useState([]);
  const [messageInput, setMessageInput] = useState('');
  const [showChat, setShowChat] = useState(true);
  const [micEnabled, setMicEnabled] = useState(false);
  const [showEmojiPicker, setShowEmojiPicker] = useState(false);
  const [currentReaction, setCurrentReaction] = useState(null);
  
  const socketRef = useRef(null);
  const chatContainerRef = useRef(null);
  const navigate = useNavigate();
  
  // Common emojis for reactions
  const commonEmojis = ['ðŸ‘', 'â¤ï¸', 'ðŸ˜‚', 'ðŸ˜®', 'ðŸ˜¢', 'ðŸ‘', 'ðŸ”¥', 'ðŸ¤”'];
  
  // Generate a random party code on mount
  useEffect(() => {
    const randomCode = Math.random().toString(36).substring(2, 8).toUpperCase();
    setPartyCode(randomCode);
    
    // Initialize WebSocket connection
    initializeWebSocket(randomCode);
    
    return () => {
      // Clean up WebSocket on unmount
      if (socketRef.current) {
        socketRef.current.close();
      }
    };
  }, []);
  
  // Auto-scroll chat to bottom when new messages arrive
  useEffect(() => {
    if (chatContainerRef.current) {
      chatContainerRef.current.scrollTop = chatContainerRef.current.scrollHeight;
    }
  }, [chatMessages]);
  
  // Initialize WebSocket connection
  const initializeWebSocket = (roomCode) => {
    // In a real implementation, this would connect to your WebSocket server
    // For demo purposes, we're using a mock WebSocket
    socketRef.current = new MockWebSocket(`wss://api.example.com/watchparty/${roomCode}`);
    
    socketRef.current.onopen = () => {
      console.log('WebSocket connection established');
      setIsConnected(true);
      
      // Join the room
      sendWebSocketMessage({
        type: 'join',
        username: 'You (Host)',
        roomCode
      });
    };
    
    socketRef.current.onmessage = (event) => {
      const data = JSON.parse(event.data);
      handleWebSocketMessage(data);
    };
    
    socketRef.current.onclose = () => {
      console.log('WebSocket connection closed');
      setIsConnected(false);
    };
    
    socketRef.current.onerror = (error) => {
      console.error('WebSocket error:', error);
    };
  };
  
  // Handle incoming WebSocket messages
  const handleWebSocketMessage = (data) => {
    switch (data.type) {
      case 'user_joined':
        handleUserJoined(data);
        break;
      case 'user_left':
        handleUserLeft(data);
        break;
      case 'playback_update':
        handlePlaybackUpdate(data);
        break;
      case 'chat_message':
        handleChatMessage(data);
        break;
      case 'reaction':
        handleReaction(data);
        break;
      default:
        console.log('Unknown message type:', data.type);
    }
  };
  
  // Handle user joined event
  const handleUserJoined = (data) => {
    // Add user to participants list if they're not already in it
    if (!participants.some(p => p.username === data.username)) {
      setParticipants(prev => [
        ...prev,
        { id: data.userId, username: data.username, isHost: false, isSelf: false }
      ]);
      
      // Add system message to chat
      setChatMessages(prev => [
        ...prev,
        {
          id: `system-${Date.now()}`,
          type: 'system',
          content: `${data.username} joined the watch party`,
          timestamp: data.timestamp
        }
      ]);
    }
  };
  
  // Handle user left event
  const handleUserLeft = (data) => {
    // Remove user from participants list
    setParticipants(prev => prev.filter(p => p.id !== data.userId));
    
    // Add system message to chat
    setChatMessages(prev => [
      ...prev,
      {
        id: `system-${Date.now()}`,
        type: 'system',
        content: `${data.username} left the watch party`,
        timestamp: data.timestamp
      }
    ]);
  };
  
  // Handle playback update event
  const handlePlaybackUpdate = (data) => {
    if (videoRef && videoRef.current) {
      // Update video playback state
      if (data.action === 'play') {
        videoRef.current.play();
      } else if (data.action === 'pause') {
        videoRef.current.pause();
      } else if (data.action === 'seek') {
        videoRef.current.currentTime = data.currentTime;
      }
      
      // Add system message to chat
      setChatMessages(prev => [
        ...prev,
        {
          id: `system-${Date.now()}`,
          type: 'system',
          content: `${data.username} ${data.action}ed the video`,
          timestamp: data.timestamp
        }
      ]);
    }
  };
  
  // Handle chat message event
  const handleChatMessage = (data) => {
    setChatMessages(prev => [
      ...prev,
      {
        id: data.messageId,
        type: 'message',
        sender: data.username,
        content: data.message,
        timestamp: data.timestamp
      }
    ]);
  };
  
  // Handle reaction event
  const handleReaction = (data) => {
    // Show reaction animation
    setCurrentReaction({
      emoji: data.emoji,
      username: data.username,
      timestamp: Date.now()
    });
    
    // Hide reaction after 2 seconds
    setTimeout(() => {
      setCurrentReaction(null);
    }, 2000);
  };
  
  // Send WebSocket message
  const sendWebSocketMessage = (message) => {
    if (socketRef.current && isConnected) {
      socketRef.current.send(JSON.stringify(message));
    }
  };
  
  // Handle video playback events
  const handlePlay = () => {
    sendWebSocketMessage({
      type: 'playback',
      action: 'play',
      username: 'You (Host)',
      currentTime: videoRef.current ? videoRef.current.currentTime : 0
    });
  };
  
  const handlePause = () => {
    sendWebSocketMessage({
      type: 'playback',
      action: 'pause',
      username: 'You (Host)',
      currentTime: videoRef.current ? videoRef.current.currentTime : 0
    });
  };
  
  const handleSeek = () => {
    sendWebSocketMessage({
      type: 'playback',
      action: 'seek',
      username: 'You (Host)',
      currentTime: videoRef.current ? videoRef.current.currentTime : 0
    });
  };
  
  // Handle chat input
  const handleChatSubmit = (e) => {
    e.preventDefault();
    
    if (messageInput.trim()) {
      sendWebSocketMessage({
        type: 'chat',
        message: messageInput,
        username: 'You (Host)'
      });
      
      setMessageInput('');
    }
  };
  
  // Handle reaction click
  const handleReactionClick = (emoji) => {
    sendWebSocketMessage({
      type: 'reaction',
      emoji,
      username: 'You (Host)'
    });
    
    setShowEmojiPicker(false);
  };
  
  // Copy party code to clipboard
  const copyPartyCode = () => {
    navigator.clipboard.writeText(partyCode);
    alert(`Party code ${partyCode} copied to clipboard!`);
  };
  
  // Format timestamp
  const formatTimestamp = (timestamp) => {
    const date = new Date(timestamp);
    return date.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
  };
  
  // Toggle microphone
  const toggleMicrophone = () => {
    setMicEnabled(!micEnabled);
    // In a real implementation, this would enable/disable the user's microphone
  };
  
  return (
    <div className="watch-party-container">
      {/* Party info bar */}
      <div className="party-info-bar">
        <div className="party-info">
          <FaUsers className="party-icon" />
          <span>Watch Party</span>
          <div className="party-code">
            <span>{partyCode}</span>
            <button className="copy-button" onClick={copyPartyCode}>
              <FaCopy />
            </button>
          </div>
        </div>
        <div className="party-controls">
          <button 
            className={`control-button ${showChat ? 'active' : ''}`}
            onClick={() => setShowChat(!showChat)}
          >
            <FaComments />
          </button>
          <button 
            className={`control-button ${micEnabled ? 'active' : ''}`}
            onClick={toggleMicrophone}
          >
            {micEnabled ? <FaMicrophone /> : <FaMicrophoneSlash />}
          </button>
          <button 
            className="control-button invite-button"
            onClick={() => alert(`Invite your friends using code: ${partyCode}`)}
          >
            <FaUserPlus />
            <span>Invite</span>
          </button>
        </div>
      </div>
      
      {/* Chat sidebar */}
      {showChat && (
        <div className="chat-sidebar">
          <div className="participants-section">
            <h3>Participants ({participants.length})</h3>
            <ul className="participants-list">
              {participants.map(participant => (
                <li key={participant.id} className="participant">
                  <span className={`participant-name ${participant.isHost ? 'host' : ''}`}>
                    {participant.username}
                    {participant.isHost && <span className="host-badge">Host</span>}
                  </span>
                </li>
              ))}
            </ul>
          </div>
          
          <div className="chat-section">
            <h3>Chat</h3>
            <div className="chat-messages" ref={chatContainerRef}>
              {chatMessages.map(message => (
                <div 
                  key={message.id} 
                  className={`chat-message ${message.type === 'system' ? 'system-message' : ''}`}
                >
                  {message.type === 'message' && (
                    <div className="message-header">
                      <span className="message-sender">{message.sender}</span>
                      <span className="message-time">{formatTimestamp(message.timestamp)}</span>
                    </div>
                  )}
                  <div className="message-content">{message.content}</div>
                </div>
              ))}
            </div>
            
            <form className="chat-input-form" onSubmit={handleChatSubmit}>
              <div className="chat-input-container">
                <input
                  type="text"
                  value={messageInput}
                  onChange={(e) => setMessageInput(e.target.value)}
                  placeholder="Type a message..."
                  className="chat-input"
                />
                <button 
                  type="button" 
                  className="emoji-button"
                  onClick={() => setShowEmojiPicker(!showEmojiPicker)}
                >
                  <FaSmile />
                </button>
                {showEmojiPicker && (
                  <div className="emoji-picker">
                    {commonEmojis.map(emoji => (
                      <button
                        key={emoji}
                        type="button"
                        className="emoji"
                        onClick={() => handleReactionClick(emoji)}
                      >
                        {emoji}
                      </button>
                    ))}
                  </div>
                )}
              </div>
              <button type="submit" className="send-button">Send</button>
            </form>
          </div>
        </div>
      )}
      
      {/* Reaction display */}
      {currentReaction && (
        <div className="reaction-display">
          <div className="reaction-emoji">{currentReaction.emoji}</div>
          <div className="reaction-username">{currentReaction.username}</div>
        </div>
      )}
    </div>
  );
};

export default WatchParty;
