import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { FaArrowLeft } from 'react-icons/fa';
import ReactPlayer from 'react-player';
import api from '../services/api';
import './Watch.css';

const Watch = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [movie, setMovie] = useState(null);
  const [videoUrl, setVideoUrl] = useState('');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchMovieAndVideo = async () => {
      try {
        setLoading(true);
        
        // Fetch movie details
        const movieData = await api.getMovieDetails(id);
        setMovie(movieData);
        
        // Fetch videos
        const videos = await api.getMovieVideos(id);
        
        // Find a trailer or any video
        const trailer = videos.find(video => video.type === 'Trailer' && video.site === 'YouTube') || videos[0];
        
        if (trailer) {
          setVideoUrl(`https://www.youtube.com/watch?v=${trailer.key}`);
        } else {
          // Fallback video if no trailer is found
          setVideoUrl('https://www.youtube.com/watch?v=dQw4w9WgXcQ');
        }
      } catch (error) {
        console.error('Error fetching movie and video:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchMovieAndVideo();
  }, [id]);

  const handleBack = () => {
    navigate(-1);
  };

  if (loading) {
    return <div className="loading">Loading...</div>;
  }

  return (
    <div className="watch-page">
      <div className="watch-back" onClick={handleBack}>
        <FaArrowLeft />
        <span>Back to Browse</span>
      </div>
      
      <div className="video-container">
        {videoUrl ? (
          <ReactPlayer
            url={videoUrl}
            width="100%"
            height="100%"
            controls
            playing
            config={{
              youtube: {
                playerVars: { showinfo: 1 }
              }
            }}
          />
        ) : (
          <div className="video-error">
            <p>Sorry, no video available for this title.</p>
          </div>
        )}
      </div>
      
      {movie && (
        <div className="watch-info">
          <h1>{movie.title}</h1>
          <p>{movie.overview}</p>
        </div>
      )}
    </div>
  );
};

export default Watch;
